import { ACTIONS } from "./actions";

export const reducer = ({ cart, total, amount }, { type, payload }) => {
  switch (type) {
    case ACTIONS.CLEAR_CART:
      return { ...state, cart: [] };
    case ACTIONS.INCREASE:
      return { ...state, cart: [] };
    case ACTIONS.DECREASE:
      return { ...state, cart: [] };
    case ACTIONS.REMOVE:
      return { ...state, cart: [] };
    default:
      return state;
  }
};
