import React from "react";
import Navbar from "./components/Navbar";
import CartContainer from "./components/CartContainer";
import cartItems from "./cart-items";
import { createStore } from "redux";

const initialStore = { count: 0, name: "" };

const ACTIONS = {
  DECREASE: "DECREASE",
  INCREASE: "INCREASE",
  RESET: "RESET",
  CHANGE_NAME: "CHANGE_NAME",
};

const reducer = (state = initialStore, { type, payload }) => {
  console.log({ state, type });
  switch (type) {
    case ACTIONS.DECREASE:
      return { ...state, count: state.count - 1 };
    case ACTIONS.INCREASE:
      return { ...state, count: state.count + 1 };
    case ACTIONS.RESET:
      return { ...state, count: state.count };
    case ACTIONS.CHANGE_NAME:
      return { ...state, name: "Antisthenes" };
    default:
      return state;
  }
};

const store = createStore(reducer);

store.dispatch({ type: ACTIONS.DECREASE });
store.dispatch({ type: ACTIONS.INCREASE });
store.dispatch({ type: ACTIONS.RESET });
store.dispatch({ type: ACTIONS.CHANGE_NAME });

// console.log(store.getState());

function App() {
  // cart setup

  return (
    <main>
      <Navbar cart={store.getState()} />
      <CartContainer cart={cartItems} />
    </main>
  );
}

export default App;
